sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

//contruct a GlideRecord query to find all NeedIt task records with due date field value in the past for the same Assigned to.
	// the record cannot be in the Closed state.
	var rightNow = new GlideDateTime();

	var overdueNITask = new GlideRecord('x_58872_needit_needit_task');
	overdueNITask.addQuery('due_date','<=',rightNow);
	overdueNITask.addQuery('state','<',3);
	overdueNITask.addQuery('assigned_to',current.assigned_to);
	overdueNITask.query();

	var count =  overdueNITask.getRowCount();
	if(count>1){
		template.print("In addition to this overdue NeedIT Task, you have "+ (count-1) +" more overdue NeedIt Tasks.");
	}
})(current, template, email, email_action, event);
